import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import './register.css';

const Register = () => {
  // State for input fields
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  // State for error message
  const [errorMessage, setErrorMessage] = useState('');

  // Handlers for input changes
  const handleEmailChange = (e) => setEmail(e.target.value);
  const handleUsernameChange = (e) => setUsername(e.target.value);
  const handlePasswordChange = (e) => setPassword(e.target.value);
  const handleConfirmPasswordChange = (e) => setConfirmPassword(e.target.value);

  // Function to validate passwords and register
  const handleRegister = (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setErrorMessage('Passwords do not match.');
      return;
    }
    setErrorMessage('');
    // Proceed with registration logic...
  };

  return (
    <div className="register-container">
      <Helmet>
        <title>Register - Profiteer</title>
        <meta property="og:title" content="Register - Profiteer" />
      </Helmet>
      <div className="register-container1">
        {/* This is a placeholder for other components you might have, like a header or navigation links */}
        <header className="register-header">
          {/* Navigation links or logo */}
          <Link to="/">Home</Link>
          <Link to="/about">About Us</Link>
        </header>

        {/* Registration form starts here */}
        <div className="register-form">
          <h2>Ready to Begin Your Financial Journey?</h2>
          <input
            type="email"
            placeholder="Email"
            className="register-email input"
            value={email}
            onChange={handleEmailChange}
          />
          <input
            type="text"
            placeholder="Username"
            className="register-user-name input"
            value={username}
            onChange={handleUsernameChange}
          />
          <input
            type="password"
            placeholder="Password"
            className="register-pass input"
            value={password}
            onChange={handlePasswordChange}
          />
          <input
            type="password"
            placeholder="Confirm Password"
            className="register-pass-conf input"
            value={confirmPassword}
            onChange={handleConfirmPasswordChange}
          />
          {errorMessage && <div className="error-message">{errorMessage}</div>}
          <button
            type="submit"
            className="register-register button"
            onClick={handleRegister}
          >
            Register Now
          </button>
        </div>
        <footer className="register-footer">
          <span>© Profiteer 2024</span>
        </footer>
      </div>
    </div>
  );
};

export default Register;
